import reviewSplash from "../../assets/reviewSplash.png";
import searchSplash from "../../assets/searchSplash.png";
import booklistSplash from "../../assets/booklistSplash.png";
import searchIcon from "../../assets/search.png";

import classes from "./HomePage.module.css";

import { motion } from "framer-motion";

const resolveBody = {
   initial: {
      opacity: 0,
   },
   animate: {
      opacity: 1,
      transition: { duration: 0.75 },
   },
   exit: {
      opacity: 0,
      transition: { duration: 0.75 },
   },
};

const moveUp = {
   initial: {
      y: "-60vh",
   },
   animate: {
      y: "9vh",
      transition: {
         delay: 0.5,
         duration: 1.5,
         ease: "easeInOut",
         type: "spring",
         stiffness: 25,
      },
   },
};

const moveDown = {
   initial: {
      y: "60vh",
   },
   animate: {
      y: "-5vh",
      transition: {
         delay: 0.5,
         duration: 1.5,
         ease: "easeInOut",
         type: "spring",
         stiffness: 25,
      },
   },
};

const HomePage = () => {
   return (
      <div className={classes.homePageWrapper}>
         <div className={classes.header}>
            <div className={classes.headerTop}>
               <div className={classes.logo}>Falling Book</div>
            </div>
            <div className={classes.headerBottom}>
               <div className={classes.headerBottomSearch}>
                  <input
                     placeholder="Search for titles or authors..."
                     type="text"
                     className={classes.searchInput}
                  />
                  <button className={classes.searchButton}>
                     <img src={searchIcon} className={classes.icon} alt="" />
                  </button>
               </div>

               <div className={classes.headerBottomNav}>
                  <div className={classes.navLink}>Home</div>
                  <div className={classes.navLink}>Settings</div>
                  <div className={classes.navLink}>Library</div>
                  <div className={classes.navLink}>Login</div>
                  <div className={classes.navLink}>Sign Up</div>
               </div>
            </div>
         </div>
         <div className={classes.body}>
            <div className={classes.bodyColumnLeft}>
               <div className={classes.bodyRow}>
                  <div className={classes.bodyRowHeader}>Search</div>
                  <div className={classes.bodyRowContent}>
                     Search for books powered by Google Books Search API.
                  </div>
               </div>
               <div className={classes.bodyRow}>
                  <motion.div
                     variants={moveUp}
                     initial="initial"
                     animate="animate"
                     className={classes.moveUp}
                  >
                     <img
                        src={reviewSplash}
                        className={classes.splash}
                        alt=""
                     />
                  </motion.div>
               </div>
               <div className={classes.bodyRow}>
                  <div className={classes.bodyRowHeader}>Booklists</div>
                  <div className={classes.bodyRowContent}>
                     Keep your reading organized by adding books to custom
                     booklists.
                  </div>
               </div>
               <div className={classes.bodyRow} />
            </div>
            <div className={classes.bodyColumnRight}>
               <div className={classes.bodyRow}>
                  <motion.div
                     variants={moveDown}
                     initial="initial"
                     animate="animate"
                     className={classes.moveDown}
                  >
                     <img
                        src={searchSplash}
                        className={classes.splash}
                        alt=""
                     />
                  </motion.div>
               </div>
               <div className={classes.bodyRow}>
                  <div className={classes.bodyRowHeader}>Rate & Review</div>
                  <div className={classes.bodyRowContent}>
                     Rate, discuss and review your favorites!
                  </div>
               </div>
               <div className={classes.bodyRow}>
                  <motion.div
                     variants={moveDown}
                     initial="initial"
                     animate="animate"
                     className={classes.moveDown}
                  >
                     <img
                        src={booklistSplash}
                        className={classes.splash}
                        alt=""
                     />
                  </motion.div>
               </div>
            </div>
         </div>
      </div>
   );
};

export default HomePage;
