import classes from "./HomePage.module.css";
import reviewSplash from "../../assets/reviewSplash.png";
import searchSplash from "../../assets/searchSplash.png";
import booklistSplash from "../../assets/booklistSplash.png";
import searchIcon from "../../assets/search.png";

import { motion } from "framer-motion";

const moveUp = {
   initial: {
      y: "-60vh",
   },
   animate: {
      y: "9vh",
      transition: {
         delay: 0.5,
         duration: 1.5,
         ease: "easeInOut",
         type: "spring",
         stiffness: 25,
      },
   },
};

const moveDown = {
   initial: {
      y: "60vh",
   },
   animate: {
      y: "-9vh",
      transition: {
         delay: 0.5,
         duration: 1.5,
         ease: "easeInOut",
         type: "spring",
         stiffness: 25,
      },
   },
};

const HomePage = () => {
   return (
      <div className={classes.homePageWrapper}>
         <div className={classes.header}>
            <div className={classes.headerTop}>
               <div className={classes.logo}>Falling Book</div>
            </div>
            <div className={classes.headerBottom}>
               <div className={classes.headerBottomSearch}>
                  <input
                     placeholder="Search for titles or authors..."
                     type="text"
                     className={classes.searchInput}
                  />
                  <button className={classes.searchButton}>
                     <img src={searchIcon} className={classes.icon} alt="" />
                  </button>
               </div>

               <div className={classes.headerBottomNav}>
                  <div className={classes.navLink}>Home</div>
                  <div className={classes.navLink}>Settings</div>
                  <div className={classes.navLink}>Library</div>
                  <div className={classes.navLink}>Login</div>
                  <div className={classes.navLink}>Sign Up</div>
               </div>
            </div>
         </div>
         <div className={classes.bodyWrapper}>
            <motion.div
               variants={moveDown}
               initial="initial"
               animate="animate"
               className={classes.contentBottom}
            >
               <div className={classes.splashWrapper}>
                  <div className={classes.splashWrapperInner}>
                     <img
                        src={searchSplash}
                        className={classes.splash}
                        alt=""
                     />
                  </div>
               </div>
               <div className={classes.content}>
                  <div className={classes.contentInner}>
                     <div className={classes.contentTitle}>Search</div>
                     <div className={classes.contentBody}>
                        Powered by Google Books API.
                     </div>
                  </div>
               </div>
            </motion.div>

            <motion.div
               variants={moveUp}
               initial="initial"
               animate="animate"
               className={classes.contentTop}
            >
               <div className={classes.content}>
                  <div className={classes.contentInner}>
                     <div className={classes.contentTitle}>Rate and Review</div>
                     <div className={classes.contentBody}>
                        Rate and review books you've read.
                     </div>
                  </div>
               </div>
               <div className={classes.splashWrapper}>
                  <div className={classes.splashWrapperInner}>
                     <img
                        src={reviewSplash}
                        className={classes.splash}
                        alt=""
                     />
                  </div>
               </div>
            </motion.div>

            <motion.div
               variants={moveDown}
               initial="initial"
               animate="animate"
               className={classes.contentBottom}
            >
               <div className={classes.splashWrapper}>
                  <div className={classes.splashWrapperInner}>
                     <img
                        src={booklistSplash}
                        className={classes.splash}
                        alt=""
                     />
                  </div>
               </div>
               <div className={classes.content}>
                  <div className={classes.contentInner}>
                     <div className={classes.contentInner}>Organize</div>
                     <div className={classes.contentBody}>
                        Create custom booklists.
                     </div>
                  </div>
               </div>
            </motion.div>
         </div>
      </div>
   );
};

export default HomePage;
