import classes from "./HomePage.module.css";
import reviewSplash from "../../assets/reviewSplash.png";
import searchSplash from "../../assets/searchSplash.png";
import booklistSplash from "../../assets/booklistSplash.png";

import { motion } from "framer-motion";

const moveUp = {
   initial: {
      y: "-40vh",
   },
   animate: {
      y: "10vh",
      transition: { delay: 0.5, duration: 0.5, ease: "easeInOut" },
   },
};

const moveDown = {
   initial: {
      y: "40vh",
   },
   animate: {
      y: "-10vh",
      transition: { delay: 0.5, duration: 0.5, ease: "easeInOut" },
   },
};

const HomePage = () => {
   return (
      <div className={classes.homePageWrapper}>
         <div className={classes.bodyWrapper}>
            <motion.div
               variants={moveDown}
               initial="initial"
               animate="animate"
               className={classes.headerBottom}
            >
               <div className={classes.splashWrapper}>
                  <div className={classes.splashWrapperInner}>
                     <img
                        src={searchSplash}
                        className={classes.splash}
                        alt=""
                     />
                  </div>
               </div>
               <div className={classes.header}>
                  <div className={classes.headerInner}>
                     <div className={classes.headerTitle}>Search</div>
                     <div className={classes.headerContent}>
                        Powered by Google Books API.
                     </div>
                  </div>
               </div>
            </motion.div>

            <motion.div
               variants={moveUp}
               initial="initial"
               animate="animate"
               className={classes.headerTop}
            >
               <div className={classes.header}>
                  <div className={classes.headerInner}>
                     <div className={classes.headerTitle}>Rate and Review</div>
                     <div className={classes.headerContent}>
                        Rate and review books you've read.
                     </div>
                  </div>
               </div>
               <div className={classes.splashWrapper}>
                  <div className={classes.splashWrapperInner}>
                     <img
                        src={reviewSplash}
                        className={classes.splash}
                        alt=""
                     />
                  </div>
               </div>
            </motion.div>

            <motion.div
               variants={moveDown}
               initial="initial"
               animate="animate"
               className={classes.headerBottom}
            >
               <div className={classes.splashWrapper}>
                  <div className={classes.splashWrapperInner}>
                     <img
                        src={booklistSplash}
                        className={classes.splash}
                        alt=""
                     />
                  </div>
               </div>
               <div className={classes.header}>
                  <div className={classes.headerInner}>
                     <div className={classes.headerTitle}>Organize</div>
                     <div className={classes.headerContent}>
                        Create custom booklists.
                     </div>
                  </div>
               </div>
            </motion.div>
         </div>
      </div>
   );
};

export default HomePage;
